sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below

    //grab value of the parts field
    var neededPart = g_form.getValue('u_parts');

    //Add a message to the work notes section to check inventory
    g_form.setValue('work_notes', 'Be sure to check inventory for ' + neededPart + ' before completing work');
}
